mapper.select:
select studentId, openedId, pscj, kscj, zpcj
from selected
where studentId=#{studentId}

select studentId, openedId, pscj, kscj, zpcj
from selected
where openedId = #{openedId}

mapper.update:
update selected
set pscj = #{pscj}, kscj = #{kscj}
where openedId = #{openedId} and studentId = #{studentId}
